import 'materialize-css';

export async function configure(aurelia) {
  aurelia.use
    .standardConfiguration()
    .developmentLogging()
    .plugin('aurelia-materialize-bridge', b => b.useAll())

    .globalResources('shared/logger');

  if (window.Waves) {
    window.Waves.displayEffect = function() {};
  }

  await aurelia.start();
  aurelia.setRoot('app');
}
